import React from "react";
import { styled } from "styled-components";
import WorkItem from "../components/WorkItem";

const Wrapper = styled.section`
  > h2 {
    text-align: center;
    font-size: 50px;
    padding: 50px 0;
  }
`;
const WorkContent = styled.div`
  display: flex;
  padding: 60px;
  > h2 {
    text-align: center;
    border-bottom: 1px solid black;
  }
`;
const Text = styled.div`
  width: 40%;
  > h3 {
    font-size: 60px;
    margin-bottom: 30px;
  }
`;

const ImgBlock = styled.div`
  width: 60%;
  > img {
    width: 100%;
    height: 80%;
    object-fit: cover;
  }
`;

const Website = styled.div``;
const App = styled.div``;
const Practice = styled.div``;

const Work = ({ title, url, img, text }) => {
  return (
    <Wrapper>
      <h2>WORK</h2>
      <WorkContent>
        <Text>
          <Website>
            <WorkItem title={title} url={url} img={img} />
            <h3>{title}</h3>
            <a href={url}></a>
            <img src={img} alt={title} />
          </Website>

          <App>
            <h3>app</h3>
            <p>Reactを用いたアプリの制作</p>
            <a href="#">01</a>
          </App>
          <Practice>
            <h3>practice</h3>
            <a href="#">その他、模写コーディングなど</a>
          </Practice>
        </Text>
      </WorkContent>
    </Wrapper>
  );
};

export default Work;
