import React from "react";
import { StyledLink } from "react-router-dom";
import { createGlobalStyle, styled } from "styled-components";
import "typeface-darumadrop-one";

// const GlobalFontStyle = createGlobalStyle`@font-face {
//     font-family: 'Pinyon Script';
//     src: url('./Assets/Fonts/PinyonScript-Regular.ttf') format('truetype');
//   }
// `;

const Wrapper = styled.section`
  background-color: pink;
  height: 100vh;
  font-family: "Darumadrop One", sans-serif;
`;

const Title = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 60px 0;
  gap: 100px;
`;

const TitleH1 = styled.h1`
  font-size: 74px;
`;

const LinkText = styled.h2`
  font-size: 34px;
`;
const CustomStyledLink = styled(LinkText)`
  /* StyledLink の名前を変更 */
  text-decoration: none;
`;
// const StyledLink = styled(Link)`
//   text-decoration: none;
// `;
const Name = styled.p`
  //   font-family: "Pinyon Script", cursive;
`;

const Home = () => {
  return (
    <Wrapper>
      <GlobalFontStyle>
        <Title>
          <CustomStyledLink to="/about">About</CustomStyledLink>
          <TitleH1>PORTFOLIO</TitleH1>
          {/* <StyledLink to="/work">Work</StyledLink> */}
        </Title>
        <Name>KouHuiRyong</Name>
      </GlobalFontStyle>
    </Wrapper>
  );
};

export default Home;
