import React from "react";
import { styled } from "styled-components";

const WorkItemStyle = styled.section``;

const WorkItems = [
  {
    title: "website",
    links: [
      { url: "http://httpbin.org/status/200", name: "01" },
      { url: "http://httpbin.org/json", name: "02" },
    ],
    img: "./flower.jpg",
    text: "仮想サイトの制作",
  },
  {
    title: "app",
    links: [{ url: "", name: "01" }],
    img: "./flower.jpg",
    text: "Reactを用いたポケモン図鑑アプリ",
  },
  {
    title: "practice",
    links: [
      { url: "", name: "01" },
      { url: "", name: "02" },
    ],
    img: "./flower.jpg",
    text: "その他模写やマークアップなど",
  },
];

const WorkItem = () => {
  return (
    <WorkItemStyle>
      {WorkItems.map((item, index) => (
        <div key={index}>
          <h2>{item.title}</h2>
          {item.links.map((link, linkIndex) => (
            <a key={linkIndex} href={link.url}>
              {link.name}
            </a>
          ))}
          <img src={item.img} alt={item.title} />
          <p>{item.text}</p>
        </div>
      ))}
    </WorkItemStyle>
  );
};

export default WorkItem;
