import React from "react";
import { styled } from "styled-components";

const WorkItemStyle = styled.section``;

const WorkItems = [
  {
    title: "website",
    url: "",
    url: "",
    img: "./flower.jpg",
    text: "仮想サイトの制作",
  },
  {
    title: "app",
    url: "",
    img: "./flower.jpg",
    text: "Reactを用いたポケモン図鑑アプリ",
  },
  {
    title: "practice",
    url: "",
    url: "",
    url: "",
    img: "./flower.jpg",
    text: "その他模写やマークアップなど",
  },
];

const WorkItem = ({ title, url, img, text }) => {
  return (
    <WorkItemStyle>
      {WorkItems.map((item, index) => (
        <div key={index}>
          <h2>{item.title}</h2>
          <a href={item.url}>01</a>
          <img src={item.img} alt={item.title} />
          <p>{item.text}</p>
        </div>
      ))}
    </WorkItemStyle>
  );
};

export default WorkItem;
