import React from "react";
import { styled } from "styled-components";

const Wrapper = styled.section`
  background-color: pink;
  height: 100vh;
`;

const AboutContent = styled.div`
  width: 80%;
  margin: 0 auto;
`;
const AboutTitle = styled.h2`
  font-size: 50px;
  text-align: center;
  padding: 50px 0;
`;

const Content = styled.div`
  position: relative;
  display: flex;
  gap: 50px;
  &::after {
    content: "";
    display: block;
    height: 600px;
    width: 0px;
    border-right: 3px solid black;
    position: absolute;
    margin: 0 auto;
  }
`;

const Name = styled.div`
  width: 50%;
  > h3 {
    font-size: 40px;
    &:first-child {
      border-bottom: black 1px solid;
    }
  }
  > p {
    font-size: 16px;
  }
`;

const Block = styled.div`
  border-left: 1px solid black;
  padding: 10px;
`;
const BlobkContent = styled.div``;

const About = () => {
  return (
    <Wrapper>
      <AboutContent>
        <AboutTitle>
          <h2>ABOUT</h2>
        </AboutTitle>
        <Content>
          <Name>
            <h3>高希怜</h3>
            <h3>Kou HuiRyong</h3>
            <p>
              7年間幼稚園に勤務していましたが、子どもを抱っこし過ぎて腰を痛めたことから別業種に転職を決意、職業訓練を通してwebデザインやフロントに興味を持ちました。
            </p>
            職業訓練を３ヶ月間通い、フロントエンドへの関心が高まり 勉強中です。
            今はRreactやStyles-Componentを重点的に触っています。 他にも<p></p>
            <p>日本で生まれ育ったので日本語ネイティブです。</p>
          </Name>

          <Block>
            <BlobkContent>
              <h3>Skill</h3>
              <p>言語：HTML/CSS/JavaScript/jQuery/React</p>
              <p>ツール：Illustrator/Photoshop</p>
              <p>その他：Word/PowerPoint</p>
            </BlobkContent>
            <BlobkContent>
              <h3>Profile</h3>
              <p>1994.8 広島県広島市出身</p>
              <p>2015.3 幼稚園教諭</p>
              <p>2022.9 職業訓練 webデザイン科</p>
              <p>2023.4 ANEX株式会社</p>
            </BlobkContent>
            <BlobkContent>
              <h3>Contact</h3>
              <p>kou16955@gmail.com</p>
              <p>github</p>
            </BlobkContent>
          </Block>
        </Content>
      </AboutContent>
    </Wrapper>
  );
};

export default About;
