import React from "react";
import { styled } from "styled-components";
import WorkItem from "../components/WorkItem";

const Wrapper = styled.section`
  > h2 {
    text-align: center;
    font-size: 50px;
    padding: 50px 0;
  }
`;
const WorkContent = styled.div`
  display: flex;
  padding: 60px;
  > h2 {
    text-align: center;
    border-bottom: 1px solid black;
  }
`;
const Text = styled.div`
  width: 40%;
  > h3 {
    font-size: 60px;
    margin-bottom: 30px;
  }
`;

const Website = styled.div``;

const WorkItems = [
  {
    title: "website",
    links: [
      { url: "http://httpbin.org/status/200", name: "01" },
      { url: "http://httpbin.org/json", name: "02" },
    ],
    img: "./flower.jpg",
    text: "仮想サイトの制作",
  },
  {
    title: "app",
    links: [{ url: "", name: "01" }],
    img: "./flower.jpg",
    text: "Reactを用いたポケモン図鑑アプリ",
  },
  {
    title: "practice",
    links: [
      { url: "", name: "01" },
      { url: "", name: "02" },
    ],
    img: "./flower.jpg",
    text: "その他模写やマークアップなど",
  },
];

const Work = () => {
  return (
    <Wrapper>
      <h2>WORK</h2>
      <WorkContent>
        <Text>
          <Website>
            {WorkItems.map((item, index) => {
              return (
                <WorkItem
                  key={index}
                  title={item.title}
                  Links={item.Links}
                  img={item.img}
                  text={item.text}
                />
              );
            })}
          </Website>
        </Text>
      </WorkContent>
    </Wrapper>
  );
};

export default Work;
